mapper.update:
UPDATE tb_free_board	SET free_board_title =#{free_board_title},
						free_board_cont =#{free_board_cont},
						free_board_writer =#{free_board_writer}
WHERE free_board_num = #{free_board_num}

mapper.select:
SELECT REVIEW_NUM, REVIEW_TITLE, USER_ID, PRO_NUM, REVIEW_DATE, REVIEW_GRADE, REVIEW_CONT, REVIEW_IMAGE, REVIEW_HITS FROM (SELECT rownum rn, REVIEW_NUM, REVIEW_TITLE, USER_ID, PRO_NUM, REVIEW_DATE, REVIEW_GRADE, REVIEW_CONT, REVIEW_IMAGE, REVIEW_HITS FROM TB_REVIEW
AND rownum <= #{page} * #{perPageNum} AND REVIEW_NUM > 0 )
WHERE rn > (#{page} -1) * #{perPageNum}
