mapper.update:
UPDATE tb_free_board	SET FREE_BOARD_TITLE =#{FREE_BOARD_TITLE},
						FREE_BOARD_CONT =#{FREE_BOARD_CONT},
						FREE_BOARD_WRITER =#{FREE_BOARD_WRITER}
WHERE FREE_BOARD_NUM = #{FREE_BOARD_NUM}

mapper.select:
SELECT FREE_BOARD_NUM, FREE_BOARD_TITLE, FREE_BOARD_CONT, FREE_BOARD_WRITER, FREE_BOARD_DATE, FREE_BOARD_HITS FROM (SELECT /*+INDEX_DESC(tb_free_board pk_board)*/ rownum rn, FREE_BOARD_NUM, FREE_BOARD_TITLE, FREE_BOARD_CONT, FREE_BOARD_WRITER, FREE_BOARD_DATE, FREE_BOARD_HITS FROM tb_free_board
AND rownum <= #{page} * #{perPageNum} AND FREE_BOARD_NUM > 0 )
WHERE rn > (#{page} -1) * #{perPageNum}
