mapper.select:
SELECT	*
FROM	TB_USER
WHERE	USER_ID = #{USER_ID}
AND		USER_PW = #{USER_PW}


SELECT  USER_NUM, USER_ID, USER_EMAIL
FROM   (SELECT 	rownum rn,
				USER_ID,
				USER_EMAIL
  		FROM	TB_USER
		WHERE	rownum <= #{page} * #{perPageNum}
		AND		USER_NUM > 0
		)
WHERE 	rn > (#{page} -1) * #{perPageNum}


SELECT	USER_NUM, USER_ID, USER_EMAIL
FROM   (SELECT  rownum rn,
				USER_ID,
				USER_EMAIL
  		FROM	TB_USER
		WHERE	rownum <= #{page} * #{perPageNum}
		AND		USER_NUM > 0
		)
WHERE 	rn > (#{page} -1) * #{perPageNum}

select count(USER_ID)
from tb_user
where USER_ID = #{USER_ID}
